import org.codehaus.jackson.annotate.JsonIgnoreimport org.codehaus.jackson.annotate.JsonProperty/** * @author vyarmole on 04.04.17. */public class ClassDescription implements Serializable {    private static final long serialVersionUID = 4357965578219135681;    @JsonIgnore    public transient boolean alreadySet = false;    @JsonProperty    public String name;    @JsonProperty    public Boolean isAbstract;Boolean getIsAbstract() {        return isAbstract    }    void setIsAbstract(Boolean isAbstract) {        this.isAbstract = isAbstract    }    String getQualifiedName() {        return qualifiedName    }    void setQualifiedName(String qualifiedName) {        this.qualifiedName = qualifiedName    }    @JsonProperty    public String qualifiedName;    @JsonProperty    public String url;    @JsonProperty    public String comment;    @JsonProperty    public String stereotype;    @JsonProperty    public List<FieldDescription> fields = new ArrayList<>();    @JsonProperty    public List<String> inheritsFrom = new ArrayList<>();    @JsonProperty    public List<String> inheritsDescription = new ArrayList<>();    @JsonProperty    public Map<String,String> inheritedFields = new HashMap<>();    String getName() {        return name    }    void setName(String name) {        this.name = name    }    String getComment() {        return comment    }    void setComment(String comment) {        this.comment = comment    }    String getStereotype() {        return stereotype    }    void setStereotype(String stereotype) {        this.stereotype = stereotype    }    List<FieldDescription> getFields() {        return fields    }    void setFields(List<FieldDescription> fields) {        this.fields = fields    }    List<EnumItemDescription> getEnumitems() {        return enumitems    }    void setEnumitems(List<EnumItemDescription> enumitems) {        this.enumitems = enumitems    }    @JsonProperty    public List<EnumItemDescription> enumitems = new ArrayList<>();    String getUrl() {        return url    }    void setUrl(String url) {        this.url = url    }    List<String> getInheritsFrom() {        return inheritsFrom    }    void setInheritsFrom(List<String> inheritsFrom) {        this.inheritsFrom = inheritsFrom    }    List<String> getInheritsDescription() {        return inheritsDescription    }    void setInheritsDescription(List<String> inheritsDescription) {        this.inheritsDescription = inheritsDescription    }    Map<String, String> getInheritedFields() {        return inheritedFields    }    void setInheritedFields(Map<String, String> inheritedFields) {        this.inheritedFields = inheritedFields    }    @Override    public String toString() {        return "ClassDescription{" +                "alreadySet=" + alreadySet +                ", name='" + name + '\'' +                ", isAbstract=" + isAbstract +                ", qualifiedName='" + qualifiedName + '\'' +                ", url='" + url + '\'' +                ", comment='" + comment + '\'' +                ", stereotype='" + stereotype + '\'' +                ", fields=" + fields +                ", inheritsFrom=" + inheritsFrom +                ", inheritsDescription=" + inheritsDescription +                ", inheritedFields=" + inheritedFields +                ", enumitems=" + enumitems +                '}';    }}