import org.codehaus.jackson.annotate.JsonIgnoreimport org.codehaus.jackson.annotate.JsonProperty/** * @author vyarmole on 04.04.17. */public class ClassDescription implements Serializable {    private static final long serialVersionUID = 4357965578219135681;    @JsonIgnore    public transient boolean alreadySet = false;    @JsonProperty    public String name;    @JsonProperty    public String comment;    @JsonProperty    public String stereotype;    @JsonProperty    public List<FieldDescription> fields = new ArrayList<>();    String getInheritsFrom() {        return inheritsFrom    }    void setInheritsFrom(String inheritsFrom) {        this.inheritsFrom = inheritsFrom    }    @JsonProperty    public String inheritsFrom;    String getName() {        return name    }    void setName(String name) {        this.name = name    }    String getComment() {        return comment    }    void setComment(String comment) {        this.comment = comment    }    String getStereotype() {        return stereotype    }    void setStereotype(String stereotype) {        this.stereotype = stereotype    }    List<FieldDescription> getFields() {        return fields    }    void setFields(List<FieldDescription> fields) {        this.fields = fields    }    List<EnumItemDescription> getEnumitems() {        return enumitems    }    void setEnumitems(List<EnumItemDescription> enumitems) {        this.enumitems = enumitems    }    @JsonProperty    public List<EnumItemDescription> enumitems = new ArrayList<>();    @Override    public String toString() {        return "ClassDescription{" +                "name='" + name + '\'' +                ", comment='" + comment + '\'' +                ", stereotype='" + stereotype + '\'' +                ", fields=" + fields +                ", enumitems=" + enumitems +                '}';    }}