import org.codehaus.jackson.annotate.JsonProperty/** * @author vyarmole on 04.04.17. */public class EnumItemDescription implements Serializable {    private static final long serialVersionUID = 3832410563237801017;    @JsonProperty    public String name;    String getName() {        return name    }    void setName(String name) {        this.name = name    }    String getComment() {        return comment    }    void setComment(String comment) {        this.comment = comment    }    @JsonProperty    public String comment;    @Override    public String toString() {        return "EnumItemDescription{" +                "name='" + name + '\'' +                ", comment='" + comment + '\'' +                '}';    }}