import org.codehaus.jackson.annotate.JsonIgnoreimport org.codehaus.jackson.annotate.JsonProperty/** * @author vyarmole on 04.04.17. */public class FieldDescription implements Serializable {    private static final long serialVersionUID = 8843494772804892790;    @JsonProperty    public String name;    @JsonIgnore    public String nameOriginal;    String getUrl() {        return url    }    void setUrl(String url) {        this.url = url    }    @JsonProperty    public String url;    @JsonProperty    public String type;    String getNameOriginal() {        return nameOriginal    }    void setNameOriginal(String nameOriginal) {        this.nameOriginal = nameOriginal    }    String getName() {        return name    }    void setName(String name) {        this.name = name    }    String getType() {        return type    }    void setType(String type) {        this.type = type    }    String getComment() {        return comment    }    void setComment(String comment) {        this.comment = comment    }    @JsonProperty    public String comment;    @Override    public String toString() {        return "FieldDescription{" +                "name='" + name + '\'' +                ", type='" + type + '\'' +                ", comment='" + comment + '\'' +                '}';    }}