import org.codehaus.jackson.annotate.JsonProperty/** * @author vyarmole on 04.04.17. */public class PackageDescription implements Serializable {    private static final long serialVersionUID = 8526917591442792901;    @JsonProperty    public String packageName;    @JsonProperty    public String domain;    @JsonProperty    public String packageId;    @JsonProperty    public String comment;    @JsonProperty    public String packageUrl;    String getPackageName() {        return packageName    }    void setPackageName(String packageName) {        this.packageName = packageName    }    String getDomain() {        return domain    }    void setDomain(String domain) {        this.domain = domain    }    String getPackageId() {        return packageId    }    void setPackageId(String packageId) {        this.packageId = packageId    }    String getComment() {        return comment    }    void setComment(String comment) {        this.comment = comment    }    String getPackageUrl() {        return packageUrl    }    void setPackageUrl(String packageUrl) {        this.packageUrl = packageUrl    }    List<ClassDescription> getClasses() {        return classes    }    void setClasses(List<ClassDescription> classes) {        this.classes = classes    }    @JsonProperty    public List<ClassDescription> classes = new ArrayList<>();    @Override    public String toString() {        return "PackageDescription{" +                "packageName='" + packageName + '\'' +                ", packageId='" + packageId + '\'' +                ", comment='" + comment + '\'' +                ", packageUrl='" + packageUrl + '\'' +                ", classes=" + classes +                '}';    }}