/** * @author vyarmole on 03.04.17. */public class Pair implements Serializable {    private Map<String, String> map;    public Pair() {        map = new HashMap<>();    }    public Pair(String key, String value) {        this();        add(key, value)    }    public void add(String key, String value) {        map.put(key, value);    }    public String getKey() {        def iterator = map.keySet().iterator()        if (iterator.hasNext()) {            return iterator.next();        }        return null;    }    public String getValue(String key) {        return map.get(key);    }    @Override    public String toString() {        return "Pair{" +                "map=" + map +                '}';    }}